using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace ProjectEuler
{
    public partial class Problems
    {
        public int Problem1()
        {
            return Enumerable.Range(1, 999).Where(x => x % 3 == 0 || x % 5 == 0).Sum();
        }

        public int Problem2()
        {
            return MathsHelper.GetFibonacciSequenceUpTo(4000000).Where(x => x % 2 == 0).Sum();
        }

        public long Problem3()
        {
            long number = 600851475143;
            List<long> primes = new List<long>();

            for (long i = 2; i <= Math.Pow(number, 0.5); i++)
            {
                if (number % i == 0)
                {
                    long otherDivisor = number / i;
                    if (MathsHelper.IsPrime(i))
                    {
                        primes.Add(i);
                    }
                    else
                    {
                        if (MathsHelper.IsPrime(otherDivisor))
                        {
                            return otherDivisor;
                        }
                    }
                }
            }

            primes.Reverse();

            return primes[0];
        }

        public int Problem4()
        {
            var list = Enumerable.Range(100, 900).ToList();
            return list.SelectMany(x => list, (x, y) => x*y).Where(SequenceHelper.IsPalindrome).Max();
        }

        public int Problem6()
        {
            return Enumerable.Range(1, 100).SelectMany(x => SequenceHelper.GetRange(x + 1, 100 - x), (x, y) => x * y).Sum() * 2;
        }

        public int Problem6Alternative()
        {
            int result = 0;
            int number = 100;
            for (int i = 1; i <= number; i++)
            {
                for (int j = i + 1; j <= number; j++)
                {
                    result = result + (i * j);
                }
            }
            return (2 * result);
        }

        public int Problem7()
        {
            return this.TheNthPrimeIs(10001);
        }

        public int Problem8()
        {
            int biggestProduct = 0;
            const string numberString = "";
            var numbers = numberString.Select(x => Int32.Parse(x.ToString())).ToList();
            numbers.Skip(4).Aggregate(numbers.Take(4), (l, i) =>
                                      {
                                          var num = l.Concat(i).ToList();
                                          biggestProduct = Math.Max(num.Product(), biggestProduct);
                                          return num.Skip(1);
                                      });

            return biggestProduct;
        }

        public int Problem8Alternative()
        {
            int biggestProduct = 0;

            string number = "";
            for (int i = 0; i <= number.Length - 5; i++)
            {
                int currentProduct = 1;
                for (int j = 0; j < 5; j++)
                {
                    currentProduct = currentProduct * Int32.Parse(number[i + j].ToString());
                }
                if (currentProduct > biggestProduct)
                {
                    biggestProduct = currentProduct;
                }
            }

            return biggestProduct;
        }

        public int Problem9()
        {
            for (int c = 0; c < 500; c++)
            {
                int aPlusB = 1000 - c;
                if (aPlusB <= 500)
                {
                    break;
                }
                else
                {
                    int product;
                    if (IsPythagoreanTriplet(aPlusB, c, out product))
                    {
                        return product;
                    }
                }
            }
            return 0;
        }

        private bool IsPythagoreanTriplet(int aPlusB, int c, out int product)
        {
            product = 0;
            for (int a = 1; aPlusB - a >= a; a++)
            {
                int b = aPlusB - a;
                if (b - a <= 500)
                {
                    if ((a * a) + (b * b) == (c * c))
                    {
                        product = a * b * c;
                        return true;
                    }
                }
            }
            return false;
        }

        public long Problem10()
        {
            Func<long,int,long> func = (l,i) => l+i;
            return MathsHelper.GetPrimesUpTo(2000000).Aggregate(0, func); 
        }

        public int TheNthPrimeIs(int whichNumerPrime)
        {
            int i = 1;
            int numberToTest = 3;
            while (i < whichNumerPrime)
            {
                if (MathsHelper.IsPrime(numberToTest))
                {
                    i++;
                }
                numberToTest++;
            }
            return numberToTest - 1;
        }
    }
}
